import {Component, OnInit} from '@angular/core';
import {CalculosService} from "../calculos.service";
import {FormBuilder, FormGroup} from "@angular/forms";
import {Router, ActivatedRoute} from "@angular/router";
import { Location } from '@angular/common';
import { Validators } from '@angular/forms';


@Component({
  selector: 'app-hombre',
  templateUrl: './hombre.component.html',
  styleUrls: ['./hombre.component.css']
})
export class HombreComponent implements OnInit {
  // @ts-ignore
  public form: FormGroup;
  constructor(
    public service:CalculosService,
    private formBuilder:FormBuilder,
    private location: Location,
    private router: Router,
    private activatedRoute: ActivatedRoute) {
  }

  async ngOnInit() {
    this.form = this.formBuilder.group({
        presupuestoName : ['',],
        cliente : [''],
        webPage : [''],
        pageCantidad : ['1'],
        idiomaCantidad : ['1'],
        seoActive : [''],
        adsActive : [''],
        totalPrecios : [''],
        fecha: new Date()
      })


    this.activatedRoute.queryParamMap.subscribe(values => {
        this.form.patchValue({
          presupuestoName : values.get('presupuestoName') || '',
          cliente :  values.get('cliente') || '',
          webPage :  values.get('webPage') || '',
          pageCantidad :  values.get('pageCantidad') || '1',
          idiomaCantidad : values.get('idiomaCantidad') || '1',
          seoActive :  values.get('seoActive') || '',
          adsActive :  values.get('adsActive') || '',
          totalPrecios :  values.get('totalPrecios') || '0'
        });
    });
    this.form.valueChanges.subscribe(values =>{
      this.router.navigate(['hombre'],{queryParams: values})
    });
  }

  updateAllData(){
    this.service.updateAllValue(this.form);
  }

  add(valor:number){
    if (valor===1){
     if (this.form.value.pageCantidad >= 1){
       this.form.patchValue({ pageCantidad: Number(this.form.value.pageCantidad)+1 });
     }
   }
   if (valor===2){
     if (this.form.value.idiomaCantidad >= 1){
       this.form.patchValue({ idiomaCantidad: Number(this.form.value.idiomaCantidad)+1 });
     }
   }
    this.updateAllData();
}

  rest(valor:number){
    if (valor===1){
      if (this.form.value.pageCantidad > 1){
        this.form.patchValue({ pageCantidad: Number(this.form.value.pageCantidad)-1 });
      }
    }
    if (valor===2){
      if (this.form.value.idiomaCantidad > 1){
        this.form.patchValue({ idiomaCantidad: Number(this.form.value.idiomaCantidad)-1 });
      }
    }
    this.updateAllData();
  }

  send(){
    this.service.send(this.form);
    this.form.reset()
    this.service.price=0;
    this.service.extras=0;
    this.form.patchValue({ idiomaCantidad: 1 });
    this.form.patchValue({ pageCantidad: 1 });

  }

  }

<div class="container-fluid  d-flex  justify-content-center gap-3 flex-wrap ">

  <div class=" mt-5 p-3 border border-primary border-radius-6   d-flex flex-column min">
   <p class="fs-3">Que quieres hacer?</p>
   <form [formGroup]="form"  (submit)="send()" method="get"   class="border border-primary p-2 mb-2 rounded-7">
   <div class="my-2">
     <input  formControlName="presupuestoName" class="" type="text" id="presupuestoName">
     <label class="mx-2" for="presupuestoName">Nombre del presupuesto.</label>
   </div>

     <div class="my-2">
       <input  formControlName="cliente" class="" type="text" id="cliente">
       <label class="mx-2" for="cliente">Nombre del cliente.</label>
     </div>

      <div class="mx-2 form-check ">
       <input class=" form-check-input" type="checkbox" formControlName="webPage" (change)="updateAllData()"   id="flexCheckDefault">
       <label class="form-check-label" for="flexCheckDefault">
         Una pagina web +(500 €)
       </label>
     </div>
     <div class="d-flex flex-column gap-2 border-dark border  p-3 rounded-5 my-2" *ngIf="form.value.webPage">
       <label class="d-flex w-100 justify-content-around align-items-center" for="numeroPaginas">
         Numero de paginas
         <button type="button" class="px-3 btn btn-danger border  rounded-6  my-1" (click)="add(1)">+</button>
         <input class="text-center w-25" type="number" min="1" formControlName="pageCantidad" id="numeroPaginas">
         <button type="button" class="px-3 btn btn-danger border  rounded-6  my-1" (click)="rest(1)">-</button>
         <a data-bs-toggle="modal" data-bs-target="#exampleModal"><mat-icon class="material-icons orange600" aria-hidden="false" >info</mat-icon></a>
       </label>
       <label class="d-flex w-100 justify-content-around align-items-center" for="idiomaCantidad">
         Numero de idiomas
         <button type="button" class="px-3 btn btn-danger border  rounded-6 my-1" (click)="add(2)">+</button>
         <input class="text-center w-25" type="number" min="1" formControlName="idiomaCantidad"  id="idiomaCantidad">
         <button type="button" class="px-3 btn btn-danger border  rounded-6  my-1" (click)="rest(2)">-</button>
         <a data-bs-toggle="modal" data-bs-target="#exampleModal"><mat-icon class="material-icons orange600" aria-hidden="false" >info</mat-icon></a>
       </label>
     </div>
     <div class="mx-2 form-check">
       <input class="form-check-input" type="checkbox" formControlName="seoActive" (change)="updateAllData()"  id="flexCheckChecked" >
       <label class="form-check-label" for="flexCheckChecked">
         Una consultoria SEO +(300 €)
       </label>
     </div>
     <div class="mx-2 form-check">
       <input class="form-check-input" type="checkbox" formControlName="adsActive" (change)="updateAllData()"  id="flexCheckChecked1" >
       <label class="form-check-label" for="flexCheckChecked1">
         Una campaya de Google Ads +(200 €)
       </label>
     </div>
     <p class="mx-2 fs-3 my-2 "  >Precio: {{service.price}} </p>
     <p class="mx-2 fs-3 ">Extra: {{service.extras}} </p>
     <button  class="btn btn-outline-primary w-100 my-2" (submit)="send()"  *ngIf="form.valid">Guardar presupuesto</button>
 </form>
   <button class="btn btn-outline-primary" routerLink="" >Home</button>
 </div>

  <app-pressupost-list  class="mt-5 p-3 border border-primary   border-radius-6   d-flex flex-column min" *ngIf="service.guardarPresupuestos.length!=0"></app-pressupost-list>
</div>

<div class="modal fade " id="exampleModal" tabindex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div class="modal-dialog modal-top">
      <p class="p-2 fondo text-muted border-radius-6 ">En este componente debes indicar el número de páginas y el número de idiomas que deseas para tu proyecto.</p>
  </div>
</div>
